mapper.select:
select *
from person
where card_number = #{idCardNumber, jdbcType=VARCHAR}

select name
from person
where card_number = #{idCardNumber, jdbcType=VARCHAR}

mapper.update:
update person
set name = #{name, jdbcType=VARCHAR}
where card_number = #{idCardNumber, jdbcType=VARCHAR}

mapper.delete:
delete
from person
where card_number = #{idCardNumber, jdbcType=VARCHAR}
